import{_ as e,c as d,a3 as o,o as r}from"./chunks/framework.CGQ59HuL.js";const p=JSON.parse('{"title":"","description":"","frontmatter":{"head":[["meta",{"name":"og:title","content":" | Rollkit"},{"name":"og:description","content":false}]]},"headers":[],"relativePath":"guides/centralized-sequencer.md","filePath":"guides/centralized-sequencer.md","lastUpdated":1732554592000}'),a={name:"guides/centralized-sequencer.md"};function c(s,t,n,i,l,h){return r(),d("div",null,t[0]||(t[0]=[o('<h3 id="centralized-sequencer-node" tabindex="-1">Centralized Sequencer Node <a class="header-anchor" href="#centralized-sequencer-node" aria-label="Permalink to &quot;Centralized Sequencer Node&quot;">â€‹</a></h3><p>The <code>centralized-sequencer</code> node implements a pluggable centralized sequencer scheme. It can be configured using the following flags:</p><table tabindex="0"><thead><tr><th>Flag</th><th>Usage</th><th>Default</th></tr></thead><tbody><tr><td><code>batch-time</code></td><td>Time in seconds to wait before generating a new batch</td><td><code>2</code> seconds</td></tr><tr><td><code>da_address</code></td><td>DA address</td><td><code>http://localhost:26658</code></td></tr><tr><td><code>da_auth_token</code></td><td>Auth token for the DA</td><td><code>&quot;&quot;</code></td></tr><tr><td><code>da_namespace</code></td><td>DA namespace where the sequencer submits transactions</td><td><code>&quot;&quot;</code></td></tr><tr><td><code>host</code></td><td>Centralized sequencer host</td><td><code>localhost</code></td></tr><tr><td><code>port</code></td><td>Centralized sequencer port</td><td><code>50051</code></td></tr><tr><td><code>listen-all</code></td><td>Listen on all network interfaces (0.0.0.0) instead of just localhost</td><td>disabled</td></tr><tr><td><code>metrics</code></td><td>Enable Prometheus metrics</td><td>disabled</td></tr><tr><td><code>metrics-address</code></td><td>Address to expose Prometheus metrics</td><td><code>&quot;:8080&quot;</code></td></tr></tbody></table><p>See <code>centralized-sequencer --help</code> for details.</p><p>The <code>centralized-sequencer</code> node reports Prometheus metrics when the <code>-metrics</code> flag is enabled.</p><p>By default, metrics are exported to <code>http://localhost:8080/metrics</code>.</p><p>The listening address and port can be configured with the <code>-metrics-address</code> flag.</p><p>The following metrics are available:</p><table tabindex="0"><thead><tr><th><strong>Name</strong></th><th><strong>Type</strong></th><th><strong>Tags</strong></th><th><strong>Description</strong></th></tr></thead><tbody><tr><td>sequencer_gas_price</td><td>Gauge</td><td></td><td>Gas price of the DA transaction</td></tr><tr><td>sequencer_last_blob_size</td><td>Gauge</td><td></td><td>Last blob size submitted to the DA</td></tr><tr><td>sequencer_transaction_status</td><td>Gauge</td><td></td><td>Transaction status of the DA transaction</td></tr><tr><td>sequencer_num_pending_blocks</td><td>Gauge</td><td></td><td>Number of blocks pending DA submission</td></tr><tr><td>sequencer_included_block_height</td><td>Gauge</td><td></td><td>Block height of the last DA transaction</td></tr></tbody></table>',9)]))}const g=e(a,[["render",c]]);export{p as __pageData,g as default};
