import{_ as e,c as t,a3 as l,o as i}from"./chunks/framework.CGQ59HuL.js";const u=JSON.parse('{"title":"Data Availability in Rollkit","description":"","frontmatter":{"head":[["meta",{"name":"og:title","content":"Data Availability in Rollkit | Rollkit"},{"name":"og:description","content":false}]]},"headers":[],"relativePath":"learn/data-availability.md","filePath":"learn/data-availability.md","lastUpdated":1752828596000}'),r={name:"learn/data-availability.md"};function o(n,a,s,d,c,h){return i(),t("div",null,a[0]||(a[0]=[l('<h1 id="data-availability-in-rollkit" tabindex="-1">Data Availability in Rollkit <a class="header-anchor" href="#data-availability-in-rollkit" aria-label="Permalink to &quot;Data Availability in Rollkit&quot;">​</a></h1><p>Data availability (DA) is a core of Rollkit&#39;s. Rollkit utilize&#39;s data availability ensures that all transaction data and block information required to verify the chain&#39;s state is accessible to anyone running a node or light client.</p><p>Learn more about data availability:</p><ul><li><a href="https://celestia.org/what-is-da/" target="_blank" rel="noreferrer">What is DA</a></li><li><a href="https://medium.com/zeeve/exploring-data-availability-layer-and-its-importance-in-rollups-0a4fbf2e0ffc" target="_blank" rel="noreferrer">The importance of DA for Rollups</a></li></ul><h2 id="how-rollkit-handles-data-availability" tabindex="-1">How Rollkit Handles Data Availability <a class="header-anchor" href="#how-rollkit-handles-data-availability" aria-label="Permalink to &quot;How Rollkit Handles Data Availability&quot;">​</a></h2><p>Rollkit is designed to be DA-agnostic, meaning it can integrate with different data availability layers depending on your needs. The main options are:</p><ul><li><p><strong>Local Data Availability (Local DA):</strong></p><ul><li>Used for development, testing, and local deployments.</li><li>Not secure for production, as data can be withheld by the node operator.</li></ul></li><li><p><strong>External Data Availability Layer (DA Interface):</strong></p><ul><li>Used for production and secure deployments.</li><li>Rollkit can post block data to any external DA layer that implements the Rollkit <a href="https://github.com/rollkit/rollkit/blob/main/core/da/da.go#L11" target="_blank" rel="noreferrer">DA interface</a> (e.g., Celestia).</li><li>Anyone can verify that the data is available and reconstruct the chain state, depending on the guarantees of the chosen DA layer.</li></ul></li></ul><h2 id="best-practices" tabindex="-1">Best Practices <a class="header-anchor" href="#best-practices" aria-label="Permalink to &quot;Best Practices&quot;">​</a></h2><ul><li><strong>Use Local DA only for development and testing locally.</strong></li><li><strong>Alternatively, you can use <a href="https://docs.celestia.org/how-to-guides/participate" target="_blank" rel="noreferrer">Celestia testnets</a>.</strong></li><li><strong>For production, always use a decentralized DA layer that implements the Rollkit DA interface.</strong></li></ul><h2 id="learn-more" tabindex="-1">Learn More <a class="header-anchor" href="#learn-more" aria-label="Permalink to &quot;Learn More&quot;">​</a></h2><ul><li><a href="/guides/da/local-da">Set up a local DA</a></li><li><a href="/guides/da/celestia-da">Set up Celestia DA</a></li><li><a href="https://docs.celestia.org/" target="_blank" rel="noreferrer">Celestia Docs</a></li></ul>',11)]))}const b=e(r,[["render",o]]);export{u as __pageData,b as default};
